
/* wordpress-start */
<div { ...blockProps }>
	<div class="container">
		<RichText.Content
            tagName="h1"
            className="title"
            value={ title }
        />

        <RichText.Content
            tagName="p"
            className="subtitle"
            value={ subtitle }
        />
	</div>
</div>
/* wordpress-end */